                  <small class="text-danger">Enter a valid email</small>
                </div>
                </div>

                <!-- Password input -->
                <div class="form-outline mb-4">
                  <label class="form-label" for="form4Example4">Password</label>
                  <input type="password" id="form4Example4" class="form-control" formControlName="password"/>
